    ok_text.value = colors[1]
  }
  if (colors.length < 3) {
    ok_background.value = colors[1]
    folder1.value = colors[1]
    folder2.value = colors[1]
    stairs.value = colors[1]
    folder3.value = colors[1]
    folder4.value = colors[1]
    folder5.value = colors[1]
    sun_background.value = colors[1]
    no_text.value = defaultArea.value
    rainbow7.value = defaultArea.value
    rainbow6.value = defaultArea.value
    rainbow5.value = defaultArea.value
    rainbow4.value = defaultArea.value
    rainbow3.value = defaultArea.value
    rainbow2.value = defaultArea.value

  }
}

const shuffle = () => {
  if (isResizing.value) return
  shufflePalette()
}

watch(palette, (palette, oldPalette) => {
    if (palette.colors !== oldPalette?.colors) {
      boxColors.value = palette.colors
    }
  },
  { deep: true }
)
watch(boxColors, (boxColors) => {
    changeColors(boxColors)
  },
)

onMounted(() => {
  if (!palette.value.colors) return
  changeColors(palette.value.colors)
  msnry.value = new Masonry( resizable.value, {
    itemSelector: '.grid-item',
    columnWidth: 90
  });
})
</script>

